Use vehicle;CREATE TABLE `vehicle` (   `Renavam` varchar(11) NOT NULL,   `Plate` char(7) NOT NULL,   `Uf` char(2) DEFAULT NULL,   PRIMARY KEY (`Renavam`),   UNIQUE KEY `Plate_UNIQUE` (`Plate`) ) ENGINE=MyISAM DEFAULT CHARSET=latin1;CREATE TABLE `infracao` (   `Ait` varchar(45) NOT NULL,   `Renavam` varchar(11) NOT NULL,   `DtInfracao` datetime DEFAULT NULL,   `Local` varchar(155) DEFAULT NULL,   `DtValidity` date DEFAULT NULL,   PRIMARY KEY (`Ait`),   KEY `Fk_Infracao_Vehicle_idx` (`Renavam`) ) ENGINE=MyISAM DEFAULT CHARSET=latin1;